/* General Reset */
body {
    margin: 0;
    font-family: Arial, sans-serif;
    background: linear-gradient(135deg, #0f0f1e, #1a1a3d);
    color: #fff;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
}

/* Navbar */
.navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: rgba(20, 20, 40, 0.9);
    padding: 15px 40px;
    box-shadow: 0 2px 8px rgba(0,0,0,0.4);
}

.logo {
    font-size: 24px;
    font-weight: bold;
    color: #00f2fe;
}

.nav-links a {
    margin-left: 20px;
    text-decoration: none;
    color: #fff;
    font-weight: 500;
    transition: 0.3s;
}

.nav-links a:hover {
    color: #00f2fe;
}

.btn {
    background: linear-gradient(135deg, #00f2fe, #4facfe);
    padding: 8px 16px;
    border-radius: 20px;
    color: #fff;
    text-decoration: none;
    font-weight: bold;
    transition: 0.3s;
    border: none;
    cursor: pointer;
}

.btn:hover {
    transform: scale(1.05);
    box-shadow: 0 0 12px rgba(0,242,254,0.6);
}

/* Profile Section - UPDATED FOR FLEXBOX */
.profile-section {
    display: flex;
    justify-content: center;
    align-items: flex-start; /* Aligns cards to the top */
    padding: 60px 20px;
    gap: 40px; /* Creates space between the two cards */
    flex-wrap: wrap; /* Allows cards to stack on smaller screens */
    flex: 1; /* Ensures the main section grows to push the footer down */
}

/* Profile Card */
.profile-card {
    background: rgba(255, 255, 255, 0.05);
    padding: 40px;
    border-radius: 15px;
    text-align: center;
    width: 320px;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(0, 242, 254, 0.3);
    box-shadow: 0 0 20px rgba(0, 242, 254, 0.15);
    flex-shrink: 0; /* Prevents card from shrinking */
}

.profile-pic {
    width: 120px;
    height: 120px;
    border-radius: 50%;
    margin-bottom: 15px;
    object-fit: cover;
    border: 3px solid #00f2fe;
    box-shadow: 0 0 15px rgba(0,242,254,0.5);
}

.profile-card h2 {
    font-size: 24px;
    margin: 10px 0;
    background: linear-gradient(135deg, #00f2fe, #4facfe);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

.profile-card p {
    margin: 5px 0;
    font-size: 16px;
    color: rgba(255, 255, 255, 0.85);
}

/* Teams Card Styling */
.teams-card {
    background: rgba(255, 255, 255, 0.05);
    padding: 30px 40px;
    border-radius: 15px;
    width: 380px;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(0, 242, 254, 0.3);
    box-shadow: 0 0 20px rgba(0, 242, 254, 0.15);
    flex-shrink: 0;
}

.teams-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding-bottom: 15px;
    border-bottom: 1px solid rgba(0, 242, 254, 0.3);
}

.teams-header h2 {
    margin: 0;
    color: #00f2fe;
}

#member-counter {
    font-size: 14px;
    color: rgba(255, 255, 255, 0.8);
    background: rgba(0,0,0,0.25);
    padding: 4px 12px;
    border-radius: 15px;
}

/* Member Placeholders Styling */
.team-member-list {
    display: flex;
    flex-direction: column;
    gap: 12px;
}

.member-placeholder {
    background: rgba(0, 0, 0, 0.25);
    padding: 10px 15px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    gap: 15px;
    border: 1px solid transparent;
    transition: border-color 0.3s, background-color 0.3s;
}

.member-placeholder:hover {
    background-color: rgba(0, 0, 0, 0.4);
    border-color: rgba(0, 242, 254, 0.2);
}

.member-avatar {
    width: 40px;
    height: 40px;
    flex-shrink: 0;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.05);
    border: 2px dashed rgba(0, 242, 254, 0.4);
    display: flex;
    justify-content: center;
    align-items: center;
    color: rgba(0, 242, 254, 0.6);
    font-size: 20px;
    font-weight: bold;
}

.member-placeholder span {
    color: rgba(255, 255, 255, 0.7);
}

/* Modal specific styles */
.modal {
    display: none;
    position: fixed;
    z-index: 1001;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: rgba(0,0,0,0.5);
    backdrop-filter: blur(5px);
}

.modal-content {
    background: linear-gradient(135deg, #1a1a3d, #0f0f1e);
    color: #fff;
    margin: 15% auto;
    padding: 30px;
    border: 1px solid rgba(0, 242, 254, 0.3);
    width: 80%;
    max-width: 500px;
    border-radius: 15px;
    box-shadow: 0 0 25px rgba(0, 242, 254, 0.3);
}

.close-btn {
    color: #fff;
    float: right;
    font-size: 28px;
    font-weight: bold;
    opacity: 0.7;
    transition: opacity 0.3s;
    cursor: pointer;
}

.close-btn:hover {
    opacity: 1;
}

.modal-input-group {
    margin-bottom: 20px;
}

.modal-input-group label {
    display: block;
    margin-bottom: 8px;
    font-weight: bold;
    color: #00f2fe;
}

.modal-input-group input {
    width: 100%;
    padding: 10px;
    box-sizing: border-box;
    border: 1px solid rgba(0, 242, 254, 0.3);
    border-radius: 4px;
    background: rgba(255, 255, 255, 0.05);
    color: #fff;
}

.modal-content .btn {
    display: block;
    width: 100%;
    padding: 12px;
    font-size: 1.1em;
    margin-top: 10px;
}

/* Footer */
.footer {
    text-align: center;
    padding: 15px;
    background: rgba(20, 20, 40, 0.9);
    font-size: 14px;
    color: #aaa;
    margin-top: auto;
}

/* Responsive Design */
@media (max-width: 800px) {
    .profile-section {
        flex-direction: column;
        align-items: center;
        gap: 30px;
    }
    .profile-card, .teams-card {
        width: 90%;
        max-width: 400px;
    }
}

