/* ---- Reset ---- */
* { 
  margin: 0; 
  padding: 0; 
  box-sizing: border-box; 
}

body {
  font-family: "Segoe UI", Tahoma, sans-serif;
  background: linear-gradient(135deg, #0f0c29, #302b63);
  color: #fff;
  line-height: 1.6;
  min-height: 100vh;
  overflow-x: hidden;
}

/* ---- Background Canvas ---- */
#bg-canvas {
  position: fixed;
  top: 0;
  left: 0;
  z-index: -1; /* stays behind content */
  width: 100%;
  height: 100%;
}

/* ---- Navbar ---- */
.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: rgba(255, 255, 255, 0.05);
  padding: 15px 30px;
  position: sticky;
  top: 0;
  z-index: 1000;
  border-bottom: 1px solid rgba(255, 255, 255, 0.15);
  box-shadow: 0 0 10px rgba(0, 255, 255, 0.15);
  backdrop-filter: blur(6px);
}

.logo {
  font-size: 1.8em;
  font-weight: bold;
  background: linear-gradient(135deg, #00f2fe, #4facfe);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.nav-links a {
  margin: 0 12px;
  color: #fff;
  text-decoration: none;
  font-weight: 500;
  transition: 0.3s;
}

.nav-links a:hover {
  color: #00e5ff;
}

.btn {
  background: linear-gradient(135deg, #00f2fe, #4facfe);
  padding: 8px 14px;
  border-radius: 6px;
  font-weight: 600;
  transition: background 0.3s, transform 0.2s;
}

.btn:hover {
  transform: scale(1.05);
}

.profile-btn {
  display: inline-block;
  background: rgba(255, 255, 255, 0.1);
  padding: 8px 12px;
  border-radius: 50%;
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: 0.3s;
}

.profile-btn:hover {
  background: rgba(255, 255, 255, 0.2);
}

/* ---- Hero ---- */
.hero {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 60px 40px;
  gap: 40px;
}

.hero-text {
  flex: 1;
  max-width: 50%;
}

.hero-text h2 {
  font-size: 36px;
  font-weight: 700;
  background: linear-gradient(135deg, #00f2fe, #4facfe);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: 15px;
}

.hero-text p {
  font-size: 18px;
  color: rgba(255, 255, 255, 0.85);
  line-height: 1.6;
  margin-bottom: 20px;
}

.hero-btn {
  display: inline-block;
  margin-top: 10px;
}

.hero-image {
  flex: 1;
  display: flex;
  justify-content: center;
}

.hero-image img {
  max-width: 65%;    /* smaller image */
  height: auto;
  border-radius: 12px;
  box-shadow: 0 0 15px rgba(0, 255, 255, 0.3);
  transition: transform 0.3s, box-shadow 0.3s;
}

.hero-image img:hover {
  transform: scale(1.05);
  box-shadow: 0 0 25px rgba(0, 255, 255, 0.5);
}

/* ---- Features ---- */
.features {
  display: flex;
  justify-content: center;
  padding: 40px 20px;
  gap: 20px;
}

.feature-box {
  flex: 1;
  padding: 25px;
  border-radius: 12px;
  text-align: center;
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.15);
  box-shadow: 0 0 12px rgba(0, 255, 255, 0.15);
  transition: transform 0.3s, box-shadow 0.3s;
}

.feature-box:hover {
  transform: translateY(-6px);
  box-shadow: 0 0 18px rgba(0, 255, 255, 0.3);
}

.feature-box img {
  width: 120px;         
  height: 120px;
  object-fit: contain;  
  margin-bottom: 15px;
}

.feature-box h3 {
  margin-bottom: 10px;
  font-size: 20px;
  color: #00e5ff;
}

/* ---- Footer ---- */
.footer {
  text-align: center;
  padding: 20px;
  margin-top: 40px;
  background: rgba(255, 255, 255, 0.05);
  border-top: 1px solid rgba(255, 255, 255, 0.15);
  color: rgba(255, 255, 255, 0.8);
}

/* ---- Responsive ---- */
@media (max-width: 768px) {
  .hero {
    flex-direction: column;
    text-align: center;
  }
  .hero-text, .hero-image {
    max-width: 100%;
  }
  .hero-image img {
    max-width: 90%;  
  }
  .features {
    flex-direction: column;
  }
}

